import{_ as e}from"./_plugin-vue_export-helper-c27b6911.js";import{o as _,h as c,p as n,q as d,l as t}from"./index-b6327078.js";const i={},o=s=>(n("data-v-65dd6b83"),s=s(),d(),s),a={class:"SocialResponsibility"},l=o(()=>t("h2",null,"国内行业现状",-1)),p=o(()=>t("p",null," 当前无障碍电影的发展仍然面临着一些挑战。尽管随着《我不是药神》等优秀电影的上映,无障碍电影的推广得到了一些提升,但无障碍版电影的数量仍然很有限。",-1)),r=o(()=>t("p",null,"版权问题一直是制约无障碍电影发展的重要因素之一。由于版权限制，无障碍电影的制作和传播受到了一定的限制，这直接影响了无障碍电影的数量和质量。 许多电影制片厂商担心无障碍电影的版权保护问题，不愿意轻易将电影制作成无障碍版本，限制了无障碍电影的发展空间。此外，无障碍电影场地和设备的缺乏， 也导致观众无法获得良好的观影体验。",-1)),u=o(()=>t("p",null,"近年来，国家一直在加大对无障碍电影的扶持力度，推动电影院线配备无障碍设施，提高观影条件；加强无障碍电影的专业人才培养，提高字幕翻译、 音频描述等质量；提高社会对无障碍电影的认知度。 文娱无障碍项目成立后，更是致力于打造可满足目标人群（视听障碍、弱视弱听、银发人群）或环境受限人群（使用场景限制无法用眼观看、需静音等）， 从操作使用（符合WCAG2.1国际无障碍标准）到内容均可无障碍便捷获取高品质、高艺术性的文娱作品（添加人声口述解说旁白、听障字幕、手语翻译画面等）， 让每个人都可平等获得精彩文娱作品带来的精神享受，让娱乐更有温度。",-1)),h=o(()=>t("p",null,"2023年，文娱无障碍项目组成员番石榴文化、译迩科技等，通过举办无障碍电影展映、微展览等形式，向公众普及无障碍电影的价值和意义， 增加观众对无障碍电影的接受度。通过多种途径，调动社会各界的支持，共同推动无障碍电影事业的发展。",-1)),f=[l,p,r,u,h];function m(s,I){return _(),c("div",a,f)}const y=e(i,[["render",m],["__scopeId","data-v-65dd6b83"]]);export{y as default};
